                .map-card {
                    display: flex;
                    flex-direction: column;
                    max-width: 100%;

                    .yandex-location-link {
                        position: relative;
                        margin-top: 15px;
                        right: 0;
                        bottom: 0;
                    }
                }
            }
        }  
    }
    
}

@media (max-width: 576px) {
    .work-time-section {
        margin: 50px auto 150px;

        &-info {
            max-width: 380px;
            padding: 0 15px;
        }

        .bird-img {
            right: calc(400px / 1920px * 100%);
        }
    }
}

@media (max-width: 480px) {

    .new-app-section {
        .new-app-section-container {
            .new-app-info {

                h1 {
                    max-width: 450px;
                } 

                .new-app-install {
                    display: flex;
                    flex-direction: column;
                    align-items: flex-start;

                    &> :not(:last-child) {
                        margin-right: 0;
                        margin-left: 12px;
                    }
                }
            }
        } 
    }
    .footer {
        &-container {
            padding: 20px 10px 20px;

            .footer-text-cards {
                flex-direction: column;

                .footer-text-card {
                    width: 100%;

                    &:first-child {
                        padding-bottom: 20px;
                    }
                }
            }
    
            .footer-block-social {
                flex-direction: column;

                & > div {
                    margin-bottom: 20px;
                }

                .footer-install {

                    & > :not(:last-child) {
                        margin-right: 0;
                    }
    
                }
            }
        }
    }
}
